import React from 'react';

import ol from 'openlayers';
import 'ol/ol.css';
import {Tile as TileLayer, Vector as VectorLayer} from 'ol/layer';


class Map extends React.Component {
	componentDidMount() {

    var featuresLayer = new ol.layer.Vector({
      source: new ol.source.Vector({
        features:[],
      })
    });


    var map = new ol.Map({
      target: this.refs.mapContainer,
      layers: [
		new ol.layer.Tile({
		  source: new ol.source.OSM()
		}),
        featuresLayer
      ],
		view: new ol.View({
		      center: ol.proj.fromLonLat([27.587914,47.151726]),
		      zoom: 8
		    })
    });



	var markers = [];

	markers.push(new ol.Feature({
	  geometry: new ol.geom.Point(
	    ol.proj.fromLonLat([27.587914, 47.151726])
	  ),
	  name: '492,00'
	}));

var colors = ['red', 'green', 'blue', 'black'];
var iconStyles = [];

colors.forEach(function(color) {
  iconStyles.push(new ol.style.Style({
    image:  new ol.style.Circle({
        radius: 6,
        stroke: new ol.style.Stroke({
            color: '#fff'
        }),
        fill: new ol.style.Fill({
            color: color
        })
    })
  }))
});

var labelStyle = new ol.style.Style({
    text: new ol.style.Text({
        font: '12px Calibri,sans-serif',
        overflow: true,
        fill: new ol.style.Fill({
            color: '#000'
        }),
        stroke: new ol.style.Stroke({
            color: '#fff',
            width: 3
        }),
        textBaseline: 'bottom',
        offsetY: -8
    })
});

var vectorSource = new ol.source.Vector({
  features: markers
});
var markerVectorLayer = new ol.layer.Vector({
  source: vectorSource,
  style: function(feature) {
      var name = feature.get('name');
      var iconStyle = iconStyles[parseInt(name)%colors.length];
      labelStyle.getText().setText(name);
      return [iconStyle, labelStyle];
  }
});

map.addLayer(markerVectorLayer);
//map.getView().fit(vectorSource.getExtent(), map.getSize());



this.setState({ 
      map: map,
      featuresLayer: featuresLayer
    });

   }


  componentDidUpdate(prevProps, prevState) {
    this.state.featuresLayer.setSource(
      new ol.source.Vector({
        features: this.props.routes
      })
    );
  }

  render() {
    return <div ref="mapContainer"> </div>;
  }
}
export default Map;
