import React from 'react';
import {getMarkersLayer} from './markerLayer.js';

import ol from 'openlayers';
import 'ol/ol.css';
import {Tile as TileLayer, Vector as VectorLayer} from 'ol/layer';


class Map extends React.Component {
	componentDidMount() {

    var featuresLayer = new ol.layer.Vector({
      source: new ol.source.Vector({
        features:[],
      })
    });

    var map = new ol.Map({
      target: this.refs.mapContainer,
      layers: [
		new ol.layer.Tile({
		  source: new ol.source.OSM()
		}),
        featuresLayer
      ],
		view: new ol.View({
		      center: ol.proj.fromLonLat([27.587914,47.151726]),
		      zoom: 8
		    })
    });

	this.setState({ 
	      map: map,
	      featuresLayer: featuresLayer
	    });
   }

  componentDidUpdate(prevProps, prevState) {
    this.state.featuresLayer.setSource(
      new ol.source.Vector({
        features: this.props.routes
      })
    );

    //this.state.map.addLayer(markerVectorLayer);
    var markersArray = [
    	{
    	'latitude': 47.151726,
    	'longitude': 27.587914,
    	'name': 'Iasi'
    	},
    	{
    	'latitude': 46.7712,
    	'longitude': 23.6236,
    	'name': 'Cluj'
    	}
    ];


	var markerVectorLayer = getMarkersLayer(markersArray);
	var test = [];
	test[0] = ol.proj.transform([27.587914,47.151726], 'EPSG:4326',   'EPSG:3857');
	test[1] = ol.proj.transform([23.6236, 46.7712], 'EPSG:4326',   'EPSG:3857');

debugger
	var layerLines = new ol.layer.Vector({
	    source: new ol.source.Vector({
	        features: [new ol.Feature({
	            geometry: new ol.geom.LineString(test, 'XY'),
	            name: 'Line'
	        })]
	    })
	});

	this.state.map.addLayer(markerVectorLayer);
    this.state.map.addLayer(layerLines);

	//this.state.map.getView().fit(markerVectorLayer.getSource().getExtent(), this.state.map.getSize());
  }

  render() {
    return <div ref="mapContainer"> </div>;
  }
}
export default Map;
