import React from 'react';

import ol from 'openlayers';
import 'ol/ol.css';
import {Tile as TileLayer, Vector as VectorLayer} from 'ol/layer';

export function getRouteLayer(markersArray){
	var markers = [];
	markersArray.forEach(function(marker) {
		markers.push(ol.proj.transform([marker.longitude, marker.latitude], 'EPSG:4326',   'EPSG:3857'));
	});

 	return createRouteLayer(markers);
}

function createRouteLayer(markers){

	var routeVectorLayer = new ol.layer.Vector({
	    source: new ol.source.Vector({
	        features: [new ol.Feature({
	            geometry: new ol.geom.LineString(markers, 'XY'),
	            name: 'Line'
	        })]
	    }),
	    style: new ol.style.Style({
        stroke: new ol.style.Stroke({
          width: 6, color: [40, 40, 40, 0.8]
        })
      })
	});

	return routeVectorLayer;
}
