import React from 'react';
import Autocomplete from  'react-autocomplete';

class Overlay extends React.Component {

state = { 
			value: '' ,
			items: []
		};

	async componentDidMount() {
		var promise = await fetch('https://places.cit.api.here.com/places/v1/autosuggest?at=47.151726,27.587914&q=palas&app_id=eiPUTOVjxY1rwovi7Kwi&app_code=obXES72dtgrH1PgkTGNWNQ');
		var test = await promise.json();
		console.log(test);	
	 }

	async onChangeHandler(event, value){ 
	 	this.setState({value});
	 	var promise = await fetch(`https://places.cit.api.here.com/places/v1/autosuggest?at=47.151726,27.587914&q=${this.state.value}&app_id=eiPUTOVjxY1rwovi7Kwi&app_code=obXES72dtgrH1PgkTGNWNQ`);
		var promiseResult = await promise.json();
		this.setState({ items: promiseResult.results });
		console.log(promiseResult);	
	}

	onSelectHandler(value){
		debugger
		this.setState({ value });
	}

	render() {
    return <div className="overlay">
    			<div className="autocompleteComponent">
		    		<Autocomplete
			          value={ this.state.value }
			          inputProps={{ id: 'states-autocomplete' }}
			          wrapperStyle={{ position: 'relative', display: 'inline-block' }}
			          items={ this.state.items }
			          getItemValue={ item => item.title }
			          onChange={(event, value) => this.onChangeHandler(event, value) }
			          onSelect={ value => this.onSelectHandler(value) }
			          renderMenu={ children => (
			            <div className = "menu">
			              { children }
			            </div>
			          )}
			          renderItem={ (item, isHighlighted) => (
			            <div
			              className={`item ${isHighlighted ? 'item-highlighted' : ''}`}
			              key={ item.id } >
			              { item.title }
			            </div>
			          )}
			        />  
    			</div>
    	</div>;
  }
}
export default Overlay;
