import React from 'react';
import {getMarkersLayer} from './markerLayer.js';
import {getRouteLayer} from './routeLayer.js';

import ol from 'openlayers';
import 'ol/ol.css';
import {Tile as TileLayer, Vector as VectorLayer} from 'ol/layer';


class Map extends React.Component {
	componentDidMount() {

    var featuresLayer = new ol.layer.Vector({
      source: new ol.source.Vector({
        features:[],
      })
    });

    var map = new ol.Map({
      target: this.refs.mapContainer,
      layers: [
		new ol.layer.Tile({
		  source: new ol.source.OSM()
		}),
        featuresLayer
      ],
		view: new ol.View({
		      center: ol.proj.fromLonLat([27.587914,47.151726]),
		      zoom: 8
		    })
    });

	this.setState({ 
	      map: map,
	      featuresLayer: featuresLayer
	    });
   }

  componentDidUpdate(prevProps, prevState) {
    this.state.featuresLayer.setSource(
      new ol.source.Vector({
        features: this.props.routes
      })
    );

    var markersArray = [
    	{
    	'latitude': 47.151726,
    	'longitude': 27.587914,
    	'name': 'Iasi'
    	},
    	{
    	'latitude': 46.7712,
    	'longitude': 23.6236,
    	'name': 'Cluj'
    	}
    ];


	var markerLayer = getMarkersLayer(markersArray);

	var routeLayer = getRouteLayer(markersArray);

	this.state.map.addLayer(markerLayer);
    this.state.map.addLayer(routeLayer);

	//this.state.map.getView().fit(markerVectorLayer.getSource().getExtent(), this.state.map.getSize());
  }

  componentWillMount(){
  	const response = fetch('https://route.api.here.com/routing/7.2/calculateroute.json?waypoint0=52.5160%2C13.3779&waypoint1=52.5206%2C13.3862&mode=fastest%3Bcar%3Btraffic%3Aenabled&app_id=yr48LHf5wQsIVK9RhyOC&app_code=yr48LHf5wQsIVK9RhyOC&departure=now')
  	.then((res) => res.json())
  	.then((res) => console.log(res));

  }

  render() {
    return <div ref="mapContainer"> </div>;
  }
}
export default Map;
